import React from 'react';

class GoogleAuth extends React.Component {
    state = { isSignedIn: null}
    // we wired gspi library to project, we loaded some additional code to library,
    // then we initialized some authencation with our client Id and ask for a scope o email
    componentDidMount(){
        window.gapi.load('client:auth2', () => {
            window.gapi.client.init
            ({clientId:'113713252572-di6n1i8g3stgpl145tq1ujluka5j4fin.apps.googleusercontent.com', scope: 'email'})
            .then(() => {
                this.auth = window.gapi.auth2.getAuthInstance();
                this.setState({ isSignedIn: this.auth.isSignedIn.get()});
                this.auth.isSignedIn.listen(this.onAuthChange);
            });
        });
    };

    onAuthChange = () => {
        this.setState({ isSignedIn: this.auth.isSignedIn.get()});
    };
    
    onSignIn = () => {
        this.auth.signIn();
    };

    onSignOut = () => {
        this.auth.signOut();
    };

    renderAuthButton(){
        if(this.state.isSignedIn === null){
            return null;
        }
        else if(this.state.isSignedIn){
            return (<button onClick={this.onSignOut} className="ui red google button"><i className="google icon" />signOut</button>)
        }
        else{
            return (<button onClick={this.onSignIn} className="ui green google button"><i className="google icon" />sign in</button>)
        }
    }

    render() {
        return (
        <div>{this.renderAuthButton()}</div>
        );
    }
}

export default GoogleAuth;
